INSERT INTO `%PREFIX%_catalog_catalog1_items` VALUES ('5','3','1','1','Hantarex 32\" TV G-W Mirror','89000.00','content/files/catalog1/Hantarex_32_TV_GW_Mirror_1258707744.jpg','Planos','Hantarex','<p>ЖК телевизор Hantarex Planos cd 32 mirror tv с диагональю экрана 32 дюйма серии TV G-W.</p>\r\n<p>Телевизоры Hantarex с первых дней заслужили репутацию самых надёжных и качественных. Используя последние инновационные технологии и разработки, при изготовлении телевизоров, покупатели могут быть уверены в конечном качестве каждого телевизора. Так и Hantarex Planos cd 32 mirror tv придерживается традиции, радуя всех своей насыщенной цветовой палитрой, высокой контрастностью, яркостью и чёткостью изображения.</p>\r\n<p>&nbsp;</p>');
INSERT INTO `%PREFIX%_catalog_catalog1_items` VALUES ('6','3','2','1','Hantarex 32\" TV G-W Stripe blk','70500.00','content/files/catalog1/pict_948_1261488030.jpg','Planos','Hantarex','<p>ЖК / lcd телевизор Hantarex 32 дюйма hantarex 32 gw stripe с поддержкой высокого разрешения (теперь вы можете смотреть различные hd источники). Просмотр новейшего цифрового телевидения возможен благодаря наличию встроенного тюнера dvb-t tv . Множество других характерных особенностей, которые выгодно отличают эту модель среди прочих.</p>');
INSERT INTO `%PREFIX%_catalog_catalog1_items` VALUES ('7','3','3','1','Hantarex 42\" TV SG Full HD','140000.00','content/files/catalog1/Hantarex_42_TV_SG_Full_HD_1258708762.jpg',NULL,'Hantarex','<p>Стильный TFT LCD-телевизор Hantarex 42\" TV Full HD отвечает самым последним тенденциям модного интерьера: лицевая панель выполнена из единого листа толстого полированного стекла (НЕ пластиковая, как у большинства видеопанелей других производителей) с антибликовым покрытием. А черная рамка, окамляющая экран, с алюминиевыми полосками сверху и снизу, подчеркивают безупречное качество изображения.</p>');
INSERT INTO `%PREFIX%_catalog_catalog1_items` VALUES ('8','3','4','1','LG 19LH2000','9500.00','content/files/catalog1/LG_19LH2000_1258709108.jpg',NULL,'LG','<p>LCD телевизор LG 19LH2000</p>\r\n<p>&nbsp;</p>');
