DROP TABLE IF EXISTS `%PREFIX%_catalog_catalog1_items`;
CREATE TABLE `%PREFIX%_catalog_catalog1_items` (
  `id` int(10) unsigned NOT NULL auto_increment,
  `group_id` int(10) unsigned NOT NULL,
  `ext_id` int(10) unsigned NOT NULL,
  `available` tinyint(1) unsigned default '0',
  `name` varchar(255) default NULL,
  `price` decimal(12,2) default NULL,
  `image` varchar(255) default NULL,
  `series` varchar(255) default NULL,
  `manufacturer` enum('Hantarex','LG','Panasonic','Sony','Logitech','Marantz','Philips','Nevo') default NULL,
  `description` text,
  PRIMARY KEY  (`id`),
  KEY `group_id` (`group_id`)
) ENGINE=MyISAM DEFAULT CHARSET=utf8;
