DROP TABLE IF EXISTS `%PREFIX%_metods`;
CREATE TABLE `%PREFIX%_metods` (
  `id` int(10) unsigned NOT NULL AUTO_INCREMENT,
  `id_module` varchar(255) NOT NULL,
  `metod` varchar(255) NOT NULL,
  `caption` varchar(255) NOT NULL,
  `properties` text,
  `flag_admin` tinyint(1) DEFAULT NULL,
  PRIMARY KEY (`id`),
  KEY `id_module` (`id_module`),
  KEY `metod` (`metod`)
) ENGINE=MyISAM AUTO_INCREMENT=41 DEFAULT CHARSET=utf8 COMMENT='Методы модулей';
