DROP TABLE IF EXISTS `%PREFIX%_modules`;
CREATE TABLE `%PREFIX%_modules` (
  `id` varchar(255) NOT NULL,
  `parent_id` varchar(255) DEFAULT NULL,
  `full_name` varchar(255) DEFAULT NULL,
  `type_admin` int(2) DEFAULT NULL,
  `properties` text,
  `properties_page` text,
  `acces_label` text,
  `serialize` text,
  `module_setings` text,
  PRIMARY KEY (`id`),
  KEY `parent_id` (`parent_id`)
) ENGINE=MyISAM DEFAULT CHARSET=utf8 COMMENT='Таблица модулей';

